mapper.select:
select count(*) from comment  where 1=1 
		
	     	and date = #{parm.date}
	   	
		
	     	and news_id = #{parm.newsId}
	   	
		
	     	and product_id = #{parm.productId}
	   	
		
	     	and context = #{parm.context}
	   	
	   	
	     	and comment_id = #{parm.commentId}
	   	
		
	     	and shop_id = #{parm.shopId}
	   	
		
	     	and user_id = #{parm.userId}
	   	
		
	     	and status = #{parm.status}
	   	
	   	
	  		and c.comment_id is null

select * from comment where 1=1 
		
	     	and date = #{parm.date}
	   	
		
	     	and news_id = #{parm.newsId}
	   	
		
	     	and product_id = #{parm.productId}
	   	
		
	     	and context = #{parm.context}
	   	
	   	
	     	and comment_id = #{parm.commentId}
	   	
		
	     	and shop_id = #{parm.shopId}
	   	
		
	     	and user_id = #{parm.userId}
	   	
		
	     	and status = #{parm.status}
	   	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select c.context,c.id,n.news_title as newsTitle, c.date as date, DATE_FORMAT(c.date,'%Y-%m-%d %h:%m:%i') as dateStr, c.user_id as userId, c.`status`,u.username from comment c LEFT JOIN news n on c.news_id=n.id LEFT JOIN `user` u on c.user_id=u.id where 1=1
and n.id = #{parm.newsId}
and c.news_id is not null
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}

select count(*) from comment c LEFT JOIN news n on c.news_id=n.id LEFT JOIN `user` u on c.user_id=u.id where 1=1
and n.id = #{parm.newsId}
and c.news_id is not null

select count(*) from `comment` c LEFT JOIN product p on c.product_id=p.id LEFT JOIN `user` u on c.user_id=u.id left join `comment` c1 on c.comment_id=c1.id LEFT JOIN `user` u1 on c1.user_id=u1.id where 1=1
and p.user_id=#{parm.userId}
and c.comment_id = #{parm.commentId}
and c.comment_id is null

select p.product_name as productName, p.id as productId, p.product_price as price, c.id as commentId, c.context as content, DATE_FORMAT(c.date,'%Y-%c-%d %h:%i') as date, u.username as postName , c.user_id as userId, u1.username as receiveName from `comment` c LEFT JOIN product p on c.product_id=p.id LEFT JOIN `user` u on c.user_id=u.id left join `comment` c1 on c.comment_id=c1.id LEFT JOIN `user` u1 on c1.user_id=u1.id where 1=1
and p.user_id=#{parm.userId}
and c.comment_id = #{parm.commentId}
and c.comment_id is null
and c.news_id = #{parm.newsId}
and c.product_id = #{parm.productId}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}

select p.product_name as productName, p.id as productId, p.product_price as price, c.id as commentId, c.context as content, DATE_FORMAT(c.date,'%Y-%c-%d %h:%i') as date, u.username as postName , p.user_id as productUserId, c.user_id as commentUserId, i.image_path as imagePath, u.user_image as userImage from `comment` c LEFT JOIN product p on c.product_id=p.id LEFT JOIN `user` u on c.user_id=u.id LEFT JOIN image i on i.product_id=c.product_id and i.index_show=1 where 1=1 and comment_id is null and c.product_id is not null
and c.user_id=#{parm.userId}
and p.user_id=#{parm.productUserId}
and c.product_id = #{parm.productId}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}

select c.id as commentId, c.context as content, DATE_FORMAT(c.date,'%Y-%c-%d %h:%i') as date, u.username as postName , c.user_id as commentUserId, n.id as newsId, n.news_title as newsTitle, n.news_date as newsDate, DATE_FORMAT(n.news_date,'%Y-%c-%d %h:%i') as newsDateStr from `comment` c LEFT JOIN news n on n.id=c.news_id LEFT JOIN `user` u on c.user_id=u.id where 1=1 and comment_id is null and c.news_id is not null
and c.user_id=#{parm.userId}
and c.news_id = #{parm.newsId}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}

SELECT rc.id as subCommentId, DATE_FORMAT(rc.date,'%Y-%c-%d %h:%i') as date, rc.user_id as postId, rc.reply_to_user_id as reciveId, rc.context as content, u1.username as postName, u2.username as reciveName from `comment` c LEFT JOIN `comment` rc on rc.comment_id = c.id LEFT JOIN `user` u1 on u1.id = rc.user_id LEFT JOIN `user` u2 on u2.id = rc.reply_to_user_id where 1=1 and c.comment_id is null
and c.id=#{parm.commentId}
and c.userId=#{parm.userId}
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}
