mapper.select:
select count(*) from message  where 1=1 
		
	     	and message_status = #{parm.messageStatus}
	   	
	   	
	     	and product_id = #{parm.productId}
	   	
		
	     	and create_time = #{parm.createTime}
	   	
	   	
	     	and message_id = #{parm.messageId}
	   	
		
	     	and post_id = #{parm.postId}
	   	
		
	     	and message_context = #{parm.messageContext}
	   	
		
	     	and receive_id = #{parm.receiveId}

select * from message where 1=1 
		
	     	and message_status = #{parm.messageStatus}
	   	
	   	
	     	and product_id = #{parm.productId}
	   	
		
	     	and create_time = #{parm.createTime}
	   	
		
	     	and post_id = #{parm.postId}
	   	
		
	     	and message_context = #{parm.messageContext}
	   	
		
	     	and receive_id = #{parm.receiveId}
	   	
	   	
	     	and message_id = #{parm.messageId}
	   	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

	select count(*)
	from  message m
	LEFT JOIN `user` u on m.post_id=u.id
	LEFT JOIN shop s on s.user_id = u.id
	where 1=1
	
     	and m.message_status = #{parm.messageStatus}
   	
   	
     	and m.product_id = #{parm.productId}
   	
	
     	and m.create_time = #{parm.createTime}
   	
   	
     	and m.message_id = #{parm.messageId}
   	
	
     	and m.post_id = #{parm.postId}
   	
	
     	and m.message_context = #{parm.messageContext}
   	
	
     	and m.receive_id = #{parm.receiveId}
   	
   	
     	and s.shop_title = #{parm.shopName}
   	
	
   		and message_id is null
   	
	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

	select m.id as id, m.post_id as postId, m.message_status as `status`, m.message_context as messageContext ,
	m.id, u.username, DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%s')  as createTimeStr,
	s.shop_title as shopTitle
	from  message m
	LEFT JOIN `user` u on m.post_id=u.id
	LEFT JOIN shop s on s.user_id = u.id
	where 1=1
	
     	and m.message_status = #{parm.messageStatus}
   	
   	
     	and m.product_id = #{parm.productId}
   	
	
     	and m.create_time = #{parm.createTime}
   	
   	
     	and m.message_id = #{parm.messageId}
   	
	
     	and m.post_id = #{parm.postId}
   	
	
     	and m.message_context = #{parm.messageContext}
   	
	
     	and m.receive_id = #{parm.receiveId}
   	
   	
     	and s.shop_title = #{parm.shopName}
   	
	
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

	select m.post_id as postId, m.message_status as `status`, m.message_context as messageContext ,
	m.id, u.username, DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%s')  as createTimeStr
	from  message m
	LEFT JOIN `user` u on m.post_id=u.id
	
   		order by ${parm.orderby}
   	
	
		limit #{parm.start},#{parm.limit}

select p.product_name as productName, p.id as productId, p.product_price as price, m.id as messageId, m.message_context as content, DATE_FORMAT(m.create_time,'%Y-%c-%d %h:%i') as date, u.username as postName, m.post_id as postId, m.receive_id as receiveId, u1.username as receiveName, i.image_path as imagePath, u1.user_image as receiveImg from message m LEFT JOIN product p on m.product_id=p.id LEFT JOIN `user` u on m.post_id=u.id LEFT JOIN `user` u1 on m.receive_id=u1.id LEFT JOIN image i on i.product_id=m.product_id and i.index_show=1 where 1=1
and m.receive_id = #{parm.receiveId}
and m.post_id = #{parm.postId}
and m.message_id = #{parm.messageId}
and m.message_id is null
order by ${parm.orderby}
limit #{parm.start},#{parm.limit}
