mapper.select:
select count(*) from shop  where 1=1 

	     	and shop_status = #{parm.shopStatus}


	     	and create_time = #{parm.createTime}


	     	and shop_qualifies = #{parm.shopQualifies}


	     	and shop_description = #{parm.shopDescription}


	     	and shop_level = #{parm.shopLevel}


	     	and shop_rank = #{parm.shopRank}


	     	and shop_title like "%"#{parm.shopTitle}"%"


	     	and shop_licenes = #{parm.shopLicenes}


	     	and user_id = #{parm.userId}

select * from shop where 1=1 

	     	and shop_status = #{parm.shopStatus}


	     	and create_time = #{parm.createTime}


	     	and shop_qualifies = #{parm.shopQualifies}


	     	and shop_description = #{parm.shopDescription}


	     	and shop_level = #{parm.shopLevel}


	     	and shop_rank = #{parm.shopRank}


	     	and shop_title like "%"#{parm.shopTitle}"%"


	     	and shop_licenes = #{parm.shopLicenes}


	     	and user_id = #{parm.userId}


   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}

select s.shop_title as shopTitle, s.shop_description as description,s.shop_rank as shopRank,
s.shop_level as shopLevel, s.shop_status as shopStatus, u.username as shopOnwer 
from shop s 
LEFT JOIN `user` u on s.user_id=u.id
where 1=1

     	and s.shop_status = #{parm.shopStatus}


     	and s.create_time = #{parm.createTime}


     	and s.shop_qualifies = #{parm.shopQualifies}


     	and s.shop_description = #{parm.shopDescription}


	     	and shop_level = #{parm.shopLevel}


     	and s.shop_rank = #{parm.shopRank}


     	and s.shop_title like "%"#{parm.shopTitle}"%"


     	and s.shop_licenes = #{parm.shopLicenes}


     	and s.user_id = #{parm.userId}


   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}
