mapper.select:
select count(*) from topic  where 1=1 

	     	and topic_description = #{parm.topicDescription}


	     	and topic_title = #{parm.topicTitle}

select * from topic where 1=1 

	     	and topic_description = #{parm.topicDescription}


	     	and topic_title = #{parm.topicTitle}


   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}

select * from topic where 1=1

   		order by ${parm.orderby}


		limit #{parm.start},#{parm.limit}
